void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    float t = iTime * 5.0;
    vec3 col = 0.5 + 0.5 * cos(vec3(5.0) * p.xyx + vec3(0.0, 2.0, 4.0) + t);
    col *= vec3(0.5 + 0.5 * cos(vec2(100.0) * p.yx + vec2(3.0, -2.0) + t), 0.0);
    col *= vec3(0.5 + 0.5 * cos(50.0 * p + vec2(-2.0, 2.0) + t), 0.0);
    fragColor = vec4(col, 1.0);
}
