void mainImage( out vec4 fragColor, in vec2 fragCoord ) { 


    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    float time = log(iTime)/1.7;
    float color = 1.;

    for (float i = 0.0; i < 1.0; i++) {
         time *= time*2.;
         uv = fract(uv * 1.5) - 0.5;
         float x = i;


        float d = -length(uv) * exp(-length(uv0));
        float r = length(uv)*78.;

        float time = log(iTime)*log(iTime)*log(iTime)*log(iTime)/3.;
        if (d <= 0.80) {time *= 2.0; d *= 0.5;}
        if (d <= 0.6) {time *= 0.5; d *= 2.;}
        if (d <= 0.5) {time *= 2.0; d *= 0.5;}
        if (d <= 0.125) {time *= 0.5; d *= 2.;}


        color *= 1.3;
        d = cos(sin(d*9. + time))/8.;
        d=abs(d);
        if (d >= 0.1) {d =1.;} 


    fragColor = vec4(fract(d*.5)*color, length(uv0)*d/2., d*length(uv), 1.0);}

}
