vec3 palette( float t) {

    vec3 a = vec3(0.149,0.224,0.788);
    vec3 b = vec3(0.141,0.851,0.051);
    vec3 c = vec3(0.784,1.000,0.000);
    vec3 d = vec3(0.055,0.188,0.867);

    return a + b * cos(6.28318 * (c*t+d));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalCol = vec3(0.0);


    for (float i = 0.0; i < 1.1; i++){


    uv = fract(uv * 1.5) - .5;
    float d = length(uv) * exp(-length(uv0)) / cos(exp(sin(023. / iTime + length(finalCol)))) * (1./exp(sin(0.2 / iTime + length(finalCol))));

    d = d+ 0.1;

    vec3 col = palette(length(uv0) + iTime + i *0.4);

    d = cos(d*8. + iTime)/8.;
    d = abs(d);

    d = 0.01 /d;

    finalCol += col * d; 
    }
    fragColor = vec4(finalCol, 1.0);
}
