vec2 square(vec2 c) {
    return vec2(c.x * c.x - c.y * c.y, 2.0*c.x*c.y);
}// from https://www.shadertoy.com/view/flVfzd

float mandelbrot(vec2 c){
    vec2 z = vec2(0.0,0.0);
    float n = 0.0;
    while (abs(length(z)) <= 1.0 && n <= 100.0) {
        z = square(z) + c;
        n += 1.0;
    }
    return n/100.0;
}//https://www.codingame.com/playgrounds/2358/how-to-plot-the-mandelbrot-set

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord/iResolution.xy-0.5)*2.0;

    // Time varying pixel color
    float draw = mandelbrot(uv);

    // Output to screen
    fragColor = vec4(draw, draw, draw, 1.0);
}
