void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy; // 归一化坐标0～1， 作为取纹理用
    vec3 col = texture(iChannel0, uv).rgb;  // 取图片纹理

    vec2 mouseUV = iMouse.xy / iResolution.xy; // 鼠标点击坐标

    mouseUV = mouseUV * 2.0;

    vec2 stickerUV = uv * 2.0;

    if (mouseUV.x - stickerUV.x < 0.5 &&  mouseUV.y - stickerUV.y < 0.5 && stickerUV.x - mouseUV.x < 0.5 &&  stickerUV.y - mouseUV.y < 0.5) {
        vec4 me = texture(iChannel1, stickerUV - mouseUV + 0.5);



        float c = 1.0;
        fragColor = vec4(mix(col, me.xyz, c), 1.0);
    } else {
        fragColor = vec4(col, 1.0);

    }




}
