#define PI acos(-1.0)

vec2 rotate(vec2 v, float a) {
	float s = sin(a), c = cos(a);
	return mat2(c, -s, s, c) * v;
}

bool di(vec2 p, float r){
    return abs(p.x) + abs(p.y) <= r;
}

bool arrow(vec2 p, float sqw, float lw){
    return di(p, sqw) && (!di(vec2(p.x, p.y - lw*2.0), sqw) || abs(p.x)*sqrt(2.0) < lw);
}

void mainImage( out vec4 col, in vec2 uv )
{
    uv = 2.0*(uv - iResolution.xy*0.5) / max(iResolution.x, iResolution.y);     
    col = vec4(0.0);


    float sqw = 0.05;


    for(float i = 0.0; i < 8.0; i++)
        if(arrow(rotate(uv + sqw*vec2(-10.5 + 3.0*i, 0.0), i*0.25*PI), sqw, sqw*0.3))
            col = vec4(0.5 + 0.5*cos(iTime + vec3(10.0*uv.x, 10.0*uv.x-0.5*PI, 10.0*uv.y)), 1.0);

}
