float DrawArrow(vec2 uv, float offset) {
    float signX = sign(uv.x);

    uv.y = abs(uv.y);
    uv.x += signX * (uv.y-fract(iTime) + offset);

    float a = 3.1415926 / 4.;
    float s = sin(a); float c = cos(a);
    mat2 m = mat2(c,-s,s,c);
    uv = m * uv;
    float t1 = smoothstep(.3,.29,abs(uv.x) + abs(uv.y));
    float t2 = smoothstep(.29,.28,abs(uv.x) + abs(uv.y));
    float t = step(0.1,t1 - t2);
    return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord.xy - iResolution.xy)/iResolution.y;
    uv *= .7;
    vec3 col = vec3(0);

    float t = iTime;
    for(float i = 0.; i < 3.; i++) {
        col += DrawArrow(uv,i * .1) * vec3(0,abs(cos(t)),abs(sin(t) * cos(t)));    
    }

    fragColor = vec4(col,1.0);
}
