void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =5.*fragCoord/iResolution.xy -2.5;
    uv = fract(uv.xy+iTime+4.0*atan(uv.x));
    vec2 niReso = normalize(iResolution.xy)+1.0;
    vec2 lo = niReso/10.;
    vec2 hi = 2.*niReso/3.;
    vec3 col = vec3(0.0);
    if(uv.x <=lo.x || uv.x >= hi.x || uv.y <= lo.y || uv.y >= hi.y){}
    else{
        col +=  0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)); 
    }

    fragColor = vec4(col,1.);
}
