float circle(vec2 p, float r)
{
    return length(p)-r;
}

float distSquared(vec2 A, vec2 B)
{
    vec2 C=A-B;
    return dot(C,C);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float fade = abs(sin(iTime));
    vec2 mousePos = iMouse.xy / iResolution.xy * 2. - 1.;
    float aspect = iResolution.x/iResolution.y;
    vec2 fadeTargetPos = vec2(mousePos.x*aspect, mousePos.y);
    float maxDist;
    maxDist=distSquared(fadeTargetPos, vec2(-1.*aspect,-1.));
    maxDist=max(maxDist, distSquared(fadeTargetPos, vec2(1.*aspect,-1.)));
    maxDist=max(maxDist, distSquared(fadeTargetPos, vec2(1.*aspect,1.)));
    maxDist=max(maxDist, distSquared(fadeTargetPos, vec2(-1.*aspect,1.)));
    maxDist=sqrt(maxDist);

    vec2 uv=(2.*fragCoord-iResolution.xy)/iResolution.y;
    uv += fadeTargetPos*-1.;
    float fadePower = step(.001 ,circle(uv, (fade)*maxDist));

    vec3 col = vec3(fadePower);


    fragColor = vec4(col,1.0);
}
