float Circle(vec2 uv, vec2 pos, float radius, float blur)
{
   // get UV distance from UV origin
   float d = length(uv - pos);
   float c = smoothstep(radius, radius-blur, d);
   return c;
}

vec3 TowerOfCircles(vec2 uv, vec2 pos, float size)
{
    //transforms
    uv -= pos;
    uv /= size;

    //colors
    vec3 red = vec3(1.0, .0, .0);
    vec3 green = vec3(.0, 1.0, .0);
    vec3 blue = vec3(.0, .0, 1.0);

    // circle
    float c0 = Circle(uv,vec2(0.9, 0.35), 0.25, 0.01);
    float c1 = Circle(uv,vec2(0.9, 0.55), 0.20, 0.01);
    float c2 = Circle(uv,vec2(0.9, 0.75), 0.15, 0.01);

    // color
    vec3 c = c0*red + c1*green + c2*blue;
    return c;
}

float Rectangle(vec2 uv, vec2 pos, vec2 size)
{
    //transforms
    uv -= pos;
    uv /= size;

    float c = smoothstep(0.69, 0.70, uv.x);
    c = c * smoothstep(1.01, 1.0, uv.x);
    c = c * smoothstep(0.34, 0.35, uv.y);
    c = c * smoothstep(0.61, 0.60, uv.y);
    return c;
}

vec3 Gentleman(vec2 uv, vec2 pos, vec2 size)
{
    //transforms
    uv -= pos;
    uv /= size;

    vec3 c = TowerOfCircles(uv, vec2(0.0, 0.0), 1.0);
    c = c + Rectangle(uv, vec2(0.05, 0.75), vec2(1.0, 0.2));
    c = c + Rectangle(uv, vec2(0.31, 0.69), vec2(0.7, 0.5));

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //0.0 <> 1.0
    vec2 uv = fragCoord/iResolution.xy;
    // adjust for aspect ratio
    uv.x *= iResolution.x/iResolution.y;

    // draw gentleman
    vec3 c = Gentleman(uv, vec2(0.0, -0.05), vec2(1.0, 1.0));

    // Output to screen
    fragColor = vec4(c+.15, 1.0);
}
