void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);

    vec3 col = vec3(0.);

    vec2 uv = (fragCoord)/iResolution.y/4.0;

    uv += vec2(iTime/2.0,iTime/3.0)/16.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/4.;
    vec2 uv1 = uv;
    for(int k = 0; k < 6; k++){ 
        vec2 offset =
            vec2(sign(uv.y-uv.x)*uv.x,uv.y)*2.
            //vec2(sign(uv.y-uv.x)*sign(uv1.x-uv1.y)*mod(uv.x*2.,2.),mod(uv.y,2.)*2.)
        ;
        //offset += floor(uv.x-uv.y)/8.;

        uv1 = uv;
        uv = abs(fract(uv.yx-offset)-.5);
        if(uv.y > uv.x) col = vec3(col.yz,abs(uv.x-uv.y));
        //else uv *= uv;
    }

    fragColor = vec4(min(col*2.,vec3(1.)),1.0);
}

