void mainImage( out vec4 O, in vec2 SC )
{
    float pixel_size = 25.;
    SC = PIXELATE(SC, pixel_size);
    O = vec4( hash1to1(uint(SC.y * R.y + SC.x + iTime*0.5)) );
}

#define R                           iResolution
#define TEXF0(SC)                 ( texelFetch(iChannel0, ivec2(SC), 0) )  // (channel, integer coordinates, mipmap level)
#define TEXF1(SC)                 ( texelFetch(iChannel1, ivec2(SC), 0) )  // (channel, integer coordinates, mipmap level)
#define NMY                       ( iMouse.y / R.y )                       // in range [0.0; 1.0]
#define LMB_IS_HOLD               ( iMouse.z > 0.0 )
#define PIXELATE(SC, PIXEL_SIZE)  ( floor( (SC) / (PIXEL_SIZE) ) * (PIXEL_SIZE) )

/*  
    Returns hash in range [0.0; 1.0].
*/
float hash1to1(uint value)
{
    // Pick some enthropy source values.
    // Try different values.
    const uint entropy0 = 12345u;
    const uint entropy1 = 67890u;

    // Calculate hash.
	value += entropy1;
	value *= 445593459u;
	value ^= entropy0;

    // 1.0f / 4294967295.0f = 2.32830644e-10

    return float(value * value * value) * 2.32830644e-10;
}

void mainImage( out vec4 O, in vec2 SC )
{
    float t = 
        LMB_IS_HOLD == true ?
            0.7 + NMY * 0.3 :
            0.96;

    t = clamp(0.01, 0.99, t);

    O = 
        iFrame == 0 ?
            vec4( 0 ) :
            TEXF0(SC) * (1. - t) + TEXF1(SC) * t;
}
