float rand(vec2 n) { return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);}
float noise(vec2 n) {
const vec2 d = vec2(0.0, 1.0);
vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zto=sin(iTime/30.)*0.5+0.5;
	//vec2 uv = fragCoord.xy / iResolution.xy;
     // Normalized pixel coordinates (from 0 to 1) / centered
    vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
    uv.x=abs(uv.x);
    uv.y=-abs(uv.y);
    uv*=zto/1.;
    // polar coordinates
    vec2 st = vec2(atan(uv.x,uv.y), length(uv));
    vec2 p_d = st*zto;

    p_d.y -= iTime * .1;
    vec4 dst_map_val = vec4(noise(p_d * vec2(50)));
    vec2 dst_offset = dst_map_val.xy;
    dst_offset -= vec2(.5,.5);
    dst_offset *= 2./zto*3.14;
    dst_offset *= 0.01;
    dst_offset *= (1. - st.t);
    vec2 dist_tex_coord = st.st + dst_offset;
    fragColor = texture(iChannel0, (dist_tex_coord/st.xy*st.t)+vec2(5.,.001*iTime));
}
