void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 off = uv - vec2(0.5, 0.5);

    float sharp = cos(iTime*1.5)+3.0;

    float r1 = 0.3 + cos(uv.x*iTime*3.0)/(iTime/3.0);
    float r2 = 0.3 + cos(uv.x*iTime*4.0)/(iTime/4.0);
    float r3 = 0.3 + cos(uv.x*iTime*5.0)/(iTime/5.0);

    vec3 col = vec3(
        length(off) - r1,
        length(off) - r2,
        length(off) - r3
    ) * -sharp;

    fragColor = vec4(col,1.0);
}
