bool function( vec2 c ) {
    float g = .1 * sin(c.x * 4.)*sin(iTime);
    bool s = abs(g-c.y) < 0.01;
    return s;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = .5;
    vec2 uv = ((fragCoord-.5*iResolution.xy)/iResolution.y) / zoom;
    bool l = function(uv);
    fragColor = vec4(l, 0., 0., 1.0);
}
