void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 o_p_mouse_data = vec2(0.0, 0.0);

    //if(fragCoord == o_p_mouse_data){
    if(ivec2(fragCoord.xy) == ivec2(o_p_mouse_data)){
        fragColor = vec4(iMouse.x, iMouse.y, 0.0, 0.0);
    }else{

        // Normalized pixel coordinates (from 0 to 1)
        vec2 o_pixel_coo_nor = (fragCoord.xy-iResolution.xy*0.5)/iResolution.y;
        vec2 o_mouse_coo_nor = (iMouse.xy-iResolution.xy*0.5)/iResolution.y;

        vec4 o_iMouse_last = texture(iChannel0, o_p_mouse_data);
        vec2 o_iMouse_delta = abs(iMouse.xy - o_iMouse_last.xy);
        vec2 o_iMouse_delta_nor = (o_iMouse_delta.xy)/iResolution.xy;

        float n_length = length(o_mouse_coo_nor-o_pixel_coo_nor);
        // Output to screen
        float n_len_mouse_delta = length(o_iMouse_delta_nor);
        float n_size = 0.02+pow(n_len_mouse_delta, 1./1.4);
        float n_size_min = o_iMouse_delta_nor.x;
        float n_size_t = sin(iTime*2.)*(n_size/2.)+(n_size/2.)+n_size_min;
        n_length = smoothstep(n_size*2., n_size, n_length);

        vec4 o_col_last = texture(iChannel0, uv);
        fragColor = mix(o_col_last, (vec4(n_length)), 0.045);

    }

}
