float circleSDF(vec2 c, float r, vec2 uv)
{
    float d = length(c-uv);
    return d - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord / iResolution.y) - vec2(0.5 * iResolution.x / iResolution.y, 0.5);
    vec2 m = (iMouse.xy / iResolution.y) - vec2(0.5 * iResolution.x / iResolution.y, 0.5);


    float d1 = -circleSDF(vec2(0.5*sin(iTime/2.0)), 0.3, uv);

    float d2 = -circleSDF(vec2(0.), 0.3, uv);

    d1 = smoothstep(0.,0.1, d1+d2);

    d2 = smoothstep(0.,0.1, d2+d2);

    fragColor = vec4(d2+d1, 0., 0.1, 1.0);
}
