///
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(0.8),b,d)
#define S2(d,b) smoothstep(8.0*antialiasing(0.85),b,d)
#define PI     3.14159265
#define TWO_PI 6.28318530


vec3 DrawFigBorde(vec3 pColObj, vec3 colOut, float distObj )
{ colOut = mix(colOut,pColObj ,S2( distObj,0.0));
  colOut = mix(colOut,vec3(0.0) ,S2(abs( distObj)-0.005,0.0));
  return colOut;
}

float Hash21(vec2 p)
{
    p=fract(p*vec2(234.34,435.345));
    p+=dot(p,p+34.26);
    return fract(p.x*p.y);
}


//FabriceNeyret2, 2023-06-11
vec3 figura01(vec2 pp, vec3 colIn, vec3 colOut)
{   vec2 p=pp;
    p = abs(p);
    float d = max(p.x,p.y);
    d =  mod(d+.1,.2)-.1;
    colOut= DrawFigBorde(colIn, colOut, (d)-0.01);
    return colOut;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ vec2 uv = -1.0 + 2.0 * fragCoord.xy/iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;
    float esc=3.0;
    uv*=esc;
    float t=iTime;
    uv=vec2(uv.x,uv.y +t*0.25);

    vec3 col=vec3(1.0);

    vec2 gv=fract(uv)-0.5;
    vec2 id=floor(uv);

    float n=Hash21(id); //rango 0.0 a 1.0
    float n2=Hash21(id+vec2(1.0,-1.0)); //rango 0.0 a 1.0

    float width=0.1;
    if(n<0.5) gv.x*=-1.0;

    gv.y=abs(gv.x + gv.y)*radians(60.0);


    if(n2<0.5)
    col= figura01(gv*2.0,vec3(0.0),col)*vec3(0.85,0.0,1.0);
    //col= figura01(gv*2.0,vec3(0.0,0.1,1.0),col);
    else
    col= figura01(gv*2.0,vec3(0.0),col)*vec3(0.85,0.0,1.0);
    //col= figura01(gv*2.0,vec3(1.0,0.0,0.0),col);

    col=pow(col,  vec3(0.554545));
    fragColor = vec4(col,1.0);
}
