float shape_circle(vec2 p, float radius) {
  return length(p) - radius;
}

float shape_rect(vec2 p) {
    // Size of the rectangle can be set with mouse click
    float rx = .32 * 2.0 * iMouse.y/iResolution.xy.y;
    float ry = .18 * 2.0 * iMouse.x/iResolution.xy.x;

    return length(max(abs(p)-vec2(rx, ry),vec2(0,0)));
}

// Normalizes the coordinates to the screen resolution
vec2 squareFrame(vec2 screenSize, vec2 coord) {
  vec2 position = 2.0 * (coord.xy / screenSize.xy) - 1.0;
  position.x *= screenSize.x / screenSize.y;
  return position;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = squareFrame(iResolution.xy, fragCoord);

    float distance;


    //distance = max(shape_circle(uv, 0.2),0.0);
    distance = max(shape_rect(uv),0.0);

    if (distance > 0.0) {
        // Time varying pixel color... 
        // just to make the background less boring
        vec3 col = vec3(
            distance/3.0 - sin(0.5*iTime)/2.0,
            distance/2.0 + sin(1.1*iTime-3.14159/5.0)/2.0,
            0.5 + distance/2.0 + cos(iTime+3.14159/3.0)/2.0
        );

        // Output to screen
        fragColor.rgb = col;
        fragColor.a = 1.0;
    } else {
        vec3 col = vec3(1,1,1);

        // Output to screen
        fragColor.rgb = col;
        fragColor.a = 1.0;
    }

}
