float Hash21(vec2 p)
{
    p = fract(p *vec2(234.34, 435.345));
    p += dot(p, p + 34.32);
    return fract(p.x * p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    vec2 UV = fragCoord.xy / iResolution.xy;

    vec3 col = vec3(0.);

    uv += iTime * .2;
    uv *= 5.;
    vec2 gv = fract(uv) - .5;
    vec2 id = floor(uv);

    float n = Hash21(id);
    float width = .2 * UV.y;

    if (n < .5) gv.x *= -1.;

    //straight line
    //float d = abs(abs(gv.x + gv.y) - .5);
    //float mask = smoothstep(.01, -.01, d - width);

    //circle
    vec2 cUv = gv - sign(gv.x + gv.y + .001) * .5;
    float d = length(cUv);
    float mask = smoothstep(.01, -.01, abs(d - .5) - width);
    float angle = atan(cUv.x, cUv.y);
    float checker = mod(id.x + id.y, 2.) * 2. - 1.;
    float flow = sin(iTime + checker * angle * 10.);
    //col += flow * mask;
    //float x = fract(angle / 1.5707 + checker * iTime * .3);
    float x = angle / 1.5707 + checker * iTime * .3;
    float y = (d - (.5 - width)) / (2. * width);
    y = abs(y - .5) * 2.;
    vec2 tUv = vec2(x, y);
    col += texture(iChannel0, tUv).rgb * mask;
    col *= (1. - tUv.y);

    //if (gv.x > .48 || gv.y > .48)
    //{
    //    col = vec3(1., 0., 0.);
    //}
    // Output to screen
    fragColor = vec4(col, 1.0);
}
