vec3 palette(in float t) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.0, 0.33, 0.67);

    return a + b * cos(6.28318 * (c * t + d));
}

float func1 (in float coord, in float cells, in float t) {
    float uvf = 0.0;
    uvf = sin(coord * cells + t);
    //uvf = smoothstep(0.01, 0.0, uvf) - smoothstep(0.0, 0.01, uvf);
    return uvf;
}

void mainImage( out vec4 fragColour, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec3 colour = sin(palette(0.1 * iTime));

    vec2 uvf = vec2(0.0);
    uvf.x = func1(uv.x, 20.0, iTime);
    uvf.y = func1(uv.y, 20.0, 2.0 * iTime);

    vec3 checker = 1.0 - vec3(uvf.x * uvf.y);
    checker = 0.03/(checker);
    checker += colour;


    // Output to screen
    fragColour = vec4(checker, 1.0);
}
