vec3 clRed    = vec3(225,57,45)/255.0;
vec3 clBlue   = vec3(0,12,138)/255.0;
vec3 clGreen  = vec3(0,120,71)/255.0;
vec3 clYellow = vec3(255,185,21)/255.0;

#define MapRangeSingle(threshold,value) smoothstep(threshold-1./iResolution.y,threshold+1./iResolution.y,value)
void mainImage(out vec4 fragColor,in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy-.5;
    vec3 col = mix(clRed,clBlue,float(uv.y<0.0));
    float field1 = min(uv.x+abs(uv.y)*-.2,0.0)*.83;
    field1 = max(field1+uv.y,field1-uv.y);
    col = max(col-float(field1<0.0),0.0);
    col = mix(col,mix(vec3(1.),clYellow,float(field1<0.0)),1.0-float(MapRangeSingle(.165,abs(field1))));
    col = mix(clGreen,col,float(MapRangeSingle(.1,abs(field1))));
    col *= float(max(abs(uv.x),abs(uv.y))<.5);
    fragColor = vec4(col,1.0);
}
