void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color1 = vec3(34./255., 166./255., 179./255.);
    vec3 color2 = vec3(126./255., 214./255., 223./255.);

    float circles = sin(uv.x * 80.0) + sin(uv.y * 45.0);

    // Time varying pixel color
    vec3 col = (step(circles, sin(iTime + uv.x)*1.8) * -1. + 1.) * mix(color1, color2, (uv.x + uv.y)/2.);

    // Output to screen
    fragColor = vec4(col,circles/10. + 0.9); //alpha optimized for cineshader
}
