void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
float speed = 3.0;
float scale = 10.0;
vec2 uv = (fragCoord/iResolution.xy - 0.5) * scale;

uv -= 0.5;
uv.x *= iResolution.x / iResolution.y;
uv += 0.5;

float r = length( uv );
float z = ( sin( r - ( iTime * speed ) ) * 0.5 + 0.5 );

float sharp = step( 0.5, z );
float soft = smoothstep(0.0, 0.9, z );

// Output to screen
fragColor = vec4( vec3( soft ),1.0);
}
