void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;


    float r = .4 + .1*cos(sin(iTime*5.))*cos(atan(uv.y, uv.x)*10. + iTime * 5.);

    float d = smoothstep(r, r + .01, length(uv) );

    vec3 col = vec3(0.153 + sin(iTime) * uv.x, 0.310 - cos(iTime) * uv.y, sin(iTime)) * d;


    // Output to screen
    fragColor = vec4(col, 1.0);
}
