float upperLip(vec2 p) {
    vec2 A = vec2(-1.5, 0.4);
    vec2 B= vec2(-1.1,0.6);
    vec2 C = vec2(-0.6, 0.9);

    vec2 F=vec2(-0.2,0.65);
    vec2 G=vec2(0.25,.85);
    vec2 H=vec2(1.2,.4);

    vec2 I = vec2(-.2, .44);
    vec2 K = vec2(-1., .4);

    // Render the control points
    float d = min(distance(p,K),min(distance(p,I),min(distance(p,H),min(distance(p,G), min(distance(p,B),   min(distance(p, A),(min(distance(p, F),distance(p, C)))))))) );
    //if (d < 0.04) { return 1.0 - smoothstep(0.025, 0.034, d); };

    B+=(B-((A+C)*0.5));		//Made the curve pass through the points
    F+=(F-((C+G)*0.5));
    H+=(H-((G+I)*0.5));
    K+=(K-((I+A)*0.5));

    // Get the signed distance to bezier curve
    d = sdBezier(A, B, C, p);
    float d2 = sdBezier(C,F,G, p);
    float d3 = sdBezier(G,H,I, p);
    float d4 = sdBezier(I,K,A, p);

    float spSign =polySpline(p,A,B,C,F,G,H,I,K);

    d=min(abs(d),min(abs(d2),min(abs(d3),abs(d4))));
    if (spSign<=0.){
        d=-d;
    }
    return d;
}

float tongue(vec2 p) {
    vec2 A = vec2(-.8, 0.);
    vec2 B= vec2(-1.,0.4);
    vec2 C = vec2(-1.0, 0.9);

    vec2 F=vec2(-0.55, 0.9);
    vec2 G=vec2(-0.6,.5);
    vec2 H=vec2(-0.3,.9);

    vec2 I = vec2(.2, .9);
    vec2 K = vec2(-.3, .0);

    // Render the control points
    float d = min(distance(p,K),min(distance(p,I),min(distance(p,H),min(distance(p,G), min(distance(p,B),   min(distance(p, A),(min(distance(p, F),distance(p, C)))))))) );
    //if (d < 0.04) { return 1.0 - smoothstep(0.025, 0.034, d); };

    B+=(B-((A+C)*0.5));		//Made the curve pass through the points
    F+=(F-((C+G)*0.5));
    H+=(H-((G+I)*0.5));
    K+=(K-((I+A)*0.5));

    // Get the signed distance to bezier curve
    d = sdBezier(A, B, C, p);
    float d2 = sdBezier(C,F,G, p);
    float d3 = sdBezier(G,H,I, p);
    float d4 = sdBezier(I,K,A, p);

    float spSign =polySpline(p,A,B,C,F,G,H,I,K);

    d=min(abs(d),min(abs(d2),min(abs(d3),abs(d4))));
    if (spSign<=0.){
        d=-d;
    }
    return d;
}

float leftBottomLip(vec2 p) {
    vec2 A = vec2(-1., 0.);
    vec2 B= vec2(-1.5,0.3);
    vec2 C = vec2(-.975, -0.4);

    // Render the control points
    float d = min(distance(p,B),min(distance(p, A),distance(p, C)));
    //if (d < 0.04) { return 1.0 - smoothstep(0.025, 0.034, d); };

    B+=(B-((A+C)*0.5));		//Made the curve pass through the points

    // Get the signed distance to bezier curve
    d = sdBezier(A, B, C, p);

    float spSign =smallPolySpline(p,A,B,C);

    d=abs(d);
    if (spSign<=0.){
        d=-d;
    }
    return d;
}

float rightBottomLip(vec2 p) {
    vec2 A = vec2(.3, -0.3);
    vec2 B= vec2(.8,-0.1);
    vec2 C = vec2(1.5, 0.4);

    vec2 F = vec2(1.2, -.1);
    vec2 G= vec2(.8,-.5);
    vec2 H = vec2(.2, -.8);


    // Render the control points
    float d = min(distance(p,H),min(distance(p,G),min(distance(p,F),min(distance(p,B),min(distance(p, A),distance(p, C))))));
    //if (d < 0.04) { return 1.0 - smoothstep(0.025, 0.034, d); };

    B+=(B-((A+C)*0.5));		//Made the curve pass through the points
    F+=(F-((C+G)*0.5));
    H+=(H-((G+A)*0.5));

    // Get the signed distance to bezier curve
    d = sdBezier(A, B, C, p);
    float d2 = sdBezier(C,F,G, p);
    float d3 = sdBezier(G,H,A, p);

    float spSign = middlePolySpline(p,A,B,C,F,G,H);

    d=d=min(abs(d),min(abs(d2),abs(d3)));
    if (spSign<=0.){
        d=-d;
    }
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    float d = upperLip((p+vec2(.0,-.1)) * 1.25);
    float d2 = tongue((p+vec2(-.35,.8))*.85);
    float d3 = leftBottomLip((p+vec2(.38,.04))*2.);
    float d4 = rightBottomLip((p+vec2(-.28,-.1))*1.75);
    d = max(d4,max(d3,max(d,d2)));

    //Visualize the distance field using iq's orange/blue scheme
    fragColor = vec4(1.0) - sign(d)*vec4(1.,1.1,1.1,1.0);
    fragColor = mix(fragColor, vec4(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );
}
