// Switch on for 'boiling' effect.
#define BOILING 1

const float PHI = ((1. + sqrt(5.)) / 2.);
const float PHI_0 = (PHI-1.);
// Hue calculation based on https://www.shadertoy.com/view/7tcSRS
#define hue(v)  (.6 + .6 * cos (6.3 * (v)  + vec4 (0,23,21,0)))

// === icon message: "sound in". link: https://www.shadertoy.com/view/MtyXRW
float message(vec2 uv) { // to alter in the icon
    uv-=vec2(1.,10.); if ((uv.x<0.)||(uv.x>=32.)||(uv.y<0.)||(uv.y>=3.)) return -1.; 
    int i=1, bit=int(pow(2.,floor(32.-uv.x)));
    if (int(uv.y)==2) i=  928473456/bit; // 00110111 01010111 01100001 01110000
    if (int(uv.y)==1) i=  626348112/bit; // 00100101 01010101 01010000 01010000
    if (int(uv.y)==0) i= 1735745872/bit; // 01100111 01110101 01100001 01010000
 	return float(i-2*(i/2));
}

float psin (float x) {
    return (sin(x) + 1.0) / 2.;
}

float pcos (float x) {
    return (cos (x) + 1.0) / 2.;
}

vec3 smokey_trail (float s, float h, float t, vec2 uv) {
    float hs = 2.5 + psin (t);
    float ws = (3. + 1.*pcos (t)) / 10.;

    float resolution = 1666.0;
    float tail = 23.0;

    float calculation_steps = 33.0;
    float stepsize = 0.618;
    vec3 color = vec3(0.);
    // Algorithm based on https://www.shadertoy.com/view/7tcSRS
    for(float i=0.; i < calculation_steps; i += stepsize) {
        float res_it_decrease = sqrt (resolution - i);
        float offset_time = t + res_it_decrease * tail;
        vec2 m = ws * vec2 (
            cos (offset_time),
            sin (s * offset_time) / hs
        );

        float ift = i * .0003;
        float point_base_size = .06 - ift;
        float point_extend = .01 - ift;
        float point_density = .618 * length (uv + m);
        float d = smoothstep (
            point_base_size,
            point_extend,  
            point_density
        );

        if (1 == BOILING) {
        vec2 suv = vec2 ((uv.x - point_density), uv.y * h + point_density);
        vec3 smpl = texture (iChannel1, suv * 2.0).rgb;
        vec3 substance = mix ((d - 0.01) * smpl, d * 0.8 * hue (h).rgb, 0.618);
        color += substance;
        }
        else {
        color += d * hue (h).rgb;  
        }
    }
    color /= 1.0;

    return color;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) { 
    // Overlay by FabriceNeyret2 https://www.shadertoy.com/view/MtyXRW
    if (iResolution.y<2000.) {float c=message(fragCoord.xy/8.);if(c>=0.){fragColor=vec4(c);return;}}  

    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.x; 

    float t = iTime;

    float c = 0.7 + 0.1*psin (t/2.);

    float tColor = t/3.;
    vec3 shape_color = smokey_trail (2., c, tColor, uv); 
    float r = (PHI_0/2.) * (1. + psin (tColor));
    vec3 base_color = vec3 (0.05, 0.0618, r);

    shape_color = max (base_color, shape_color - base_color);
    fragColor = vec4 (shape_color, 1.0);
}
