vec3 color (vec3 v) {vec3 color; color[0] = v[0] / 255.0; color[1] = v[1] / 255.0; color[2] = v[2] / 255.0; return color;}

vec3 red = vec3(200.0, 16.0, 46.0);
vec3 blue = vec3(1.0, 33.0, 105.0);
vec3 white = vec3(255.0, 255.0, 255.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord / iResolution.xy;   

    vec3 col = color(blue); 

    float dy = sin(iTime * 5.0 - uv.x * 27.0) / 56.0;

    uv.y = uv.y + dy;

    if(abs(uv.x - uv.y) < 0.06 || abs(1.0 - uv.x - uv.y) < 0.06) {
        col = color(white);
    }

    if(abs(uv.x - uv.y) < 0.03 || abs(1.0 - uv.x - uv.y) < 0.03) {
        col = color(red);
    }

    if(uv.x < 0.55 && uv.x > 0.45 || uv.y > 0.42 && uv.y < 0.58) {
         col = color(white);
    }
    if(uv.x < 0.52 && uv.x > 0.48 || uv.y > 0.45 && uv.y < 0.55) {
         col = color(red);
    }

    //! Создание тени
    col -= - sin(iTime * 5.0 - uv.x * 13.0 - uv.y * 13.0) / 15.0;

    //! Уменьшение размеров флага и создание боковой волны
    if (uv.x > 0.79 + sin(iTime * 5.0 - uv.x * 17.0) / 56.0 || uv.x < 0.2
        || uv.y > 0.8 || uv.y < 0.2)
        col = color(white);

    fragColor = vec4(col, 1.0);
}
