void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 o_pix_coo_nor = (fragCoord.xy - iResolution.xy*0.5)/iResolution.y;
    vec2 o_mou_coo_nor = (iMouse.xy - iResolution.xy*0.5)/iResolution.y;

    if(iMouse.w == 0.){
        o_mou_coo_nor = vec2(0.01, 0.0);
    }

    float n_freqs = o_mou_coo_nor.x*1000.0;
    n_freqs = 222.; //tmp static
    vec2 o_opcn_big = o_pix_coo_nor*vec2(n_freqs);
    vec2 o_opcn_big_mod = fract(o_opcn_big);
    vec2 o_opcn_big_floor = floor(o_opcn_big);
    // 0.1, 0.1, 0.1, instead of 0.13, 0.16, 0.19...
    vec2 o_pix_coo_nor_subdivfloor = vec2(1.) / o_opcn_big_floor;

    float n_0to1 = o_opcn_big_mod.x;
    //https://www.desmos.com/calculator/krkptci6rn
    float n_0to1to0 = (abs((n_0to1*2.)-1.)-1.)*-1.;
    float n_dist1 = o_opcn_big_mod.x;//
    float n_freq_norm = abs(o_opcn_big_floor.x) / n_freqs;
    //todo: float n_freq_norm_logarhitmic = abs(o_opcn_big_floor.x) / n_freqs;
    float n_amp = texture(iChannel0, vec2(n_freq_norm, 0.0)).x;
    n_amp*=0.5;
    fragColor = vec4(n_0to1to0*n_amp);

    float n_delta = abs(o_pix_coo_nor.y) - n_amp;
    float n_delta_nor = n_delta / (1.-n_amp);
    if(n_delta > 0.){

        fragColor = vec4(n_delta_nor);
    }


}
