const float PI = 3.141592653589793238462643383;

float random1(float st){
  float S = dot(vec3(st),vec3(127.1,311.7,783.089));
  return fract(sin(S)*43758.5453123);
}

float smoooth(float f){
    f = 6.0 * pow(f, 5.0) - 15.0 * pow(f, 4.0) +  10.0 * pow(f, 3.0);
    return fract(f);
}

float perlin_noise(float seed){
    float p1 = random1(floor(seed));
    float p2 = random1(floor(seed) + 1.0);

    float d1 = floor(seed) - seed;
    float d2 = floor(seed) + 1.0 - seed;

    float smoothed = smoooth(fract(seed));

    return mix(dot(p1, d1), dot(p2, d2), smoothed);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    float freq1 = 25.0 * (sin(iTime) + 2.0) * 0.5;
    float freq2 = 15.0 * (cos(iTime) + 2.0) * 0.5;
    float freq3 = 5.0 * (sin(2.0 * iTime) + 2.0) * 0.5;

    float perlin1 = perlin_noise(freq1 * fragCoord[0] / iResolution.x);
    float perlin2 = perlin_noise(freq2 * fragCoord[0] / iResolution.x);
    float perlin3 = perlin_noise(freq3 * fragCoord[0] / iResolution.x);

    float Perlin1 = (perlin1 + 1.0) * 0.5;
    float Perlin2 = (perlin2 + 1.0) * 0.5;
    float Perlin3 = (perlin3 + 1.0) * 0.5;

    float p = 1.0;

    if(((Perlin1 + Perlin2 + Perlin3) / 3.0 - 0.5) * 4.0 + 0.5 < (fragCoord[1] / iResolution.y)){
        p = 0.0;
    }

    fragColor = vec4(vec3(p), 1.0);
}
