void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 0.5*fragCoord/iResolution.xy-0.25;

    vec3 col = vec3(length(sin(1000.0*uv.x-iTime*15.)));
    col += length(sin(4000.*uv.y));
    col *= vec3(0.2*length(uv.xy),0.2*uv.y,0.2*uv.x) +0.3;
    col -= length(sin(uv.xyx));


    col = step(0.45,col);

    // Output to screen
    fragColor = vec4(col,1.0);
}
