float circle(vec2 uv, float radius, vec2 blur, vec2 pos)
{
        float col = length(uv*radius - pos);

        col = smoothstep(blur.x, blur.y, col);

        return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.xy -= 0.5;

    uv.x *= iResolution.x/iResolution.y;

    //Radius
    float radius = abs(sin(iTime * 2.0)) * 3.0 + 2.0;

    //Create circle
    vec3 circle1 = vec3(circle(uv, radius, vec2(0.4, 0.55), vec2(0.0)));

    //Subtract next circle
    circle1 -= vec3(circle(uv, radius -0.2, vec2(0.4, 0.55), vec2(0.0)));

    //Set fragColor
    fragColor = vec4(1.0 - (circle1*10.0), 1.0);
}
