#define PI 3.141592653589793
#define HALF_PI 1.5707963267948966

vec3 circle(in vec2 st, float size, vec2 point){

    vec3 color = vec3(0.467,1.000,0.506) *  (1.0 - smoothstep(size, size+ 0.020, distance(st,point)));

    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x / iResolution.y;
    st.x -= 0.4;
    vec3 color = vec3(0.0);

    float size  = 0.1;

    const int n = 7;
    for(int i = 1; i < n;i++){
        float margin = float(i)*(2.0*PI)/float(n-1);
        float speed = 5.0;
        vec2 pos = vec2(sin(iTime*speed+margin)*0.2+0.5,cos(iTime*speed+margin)*0.2+0.5);
        color += circle(st, 0.06, pos); 
    }

    fragColor = vec4(color,1.0);
}
