void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

float rand(float n){return fract(sin(n) * 43758.5453123);}


float kick(float ftime)
{
  if (ftime < 0.0) return 0.0;
  float amplitude = exp(-15.0*ftime);
  return sin(6.2831*45.0*ftime)*amplitude;
}

float snare(float ftime)
{
  if (ftime < 0.0) return 0.0;
  float amplitude = exp(-45.0*ftime)+exp(-55.0*ftime);
  return (sin(6.2831*230.0*ftime))*exp(-55.0*ftime)*0.8 + rand(ftime*100.0)*exp(-45.0*ftime);
}

float ltom(float ftime)
{
  if (ftime < 0.0) return 0.0;if (ftime < 0.0) return 0.0;
  float amplitude = exp(-35.0*ftime);
  return sin(6.2831*130.0*ftime)*amplitude;
}

float lmtom(float ftime)
{
  if (ftime < 0.0) return 0.0;if (ftime < 0.0) return 0.0;
  float amplitude = exp(-35.0*ftime);
  return sin(6.2831*150.0*ftime)*amplitude;
}

float htom(float ftime)
{
  if (ftime < 0.0) return 0.0;if (ftime < 0.0) return 0.0;
  float amplitude = exp(-35.0*ftime);
  return sin(6.2831*190.0*ftime)*amplitude;
}



vec2 mainSound( int samp, float time )
{
    time = fract(time) + float(int(time) % 2);
    // A 440 Hz wave that attenuates quickly overt time
    return vec2( kick(time) + 
                 snare(time - 0.3) + 
                 ltom(time - 0.6) + 
                 lmtom(time - 0.9) + 
                 htom(time - 1.1));
}
