void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =fragCoord/iResolution.xy;
   fragColor = vec4(texture(iChannel0,uv));
}

float scale = 16.0;
bool pause = false;
bool keyPressed(int keyCode) {
	return bool(texture(iChannel1, vec2((float(keyCode) + 0.5) / 256., .5/3.)).r);   
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(keyPressed(80))pause=true;
    else pause = false;
    vec2 scaleCoord = floor(fragCoord/scale)*scale;
    vec2 scaleMouse = floor(iMouse.zw/scale)*scale;
    fragColor=texture(iChannel0,vec2((scaleCoord.x)/iResolution.x,(scaleCoord.y)/iResolution.y));
    if(length(scaleCoord-scaleMouse) < scale)fragColor=vec4(abs(fragColor.x-1.0),abs(fragColor.x-1.0),abs(fragColor.x-1.0),1.0);
      else if(!pause) {
        float neighbor = 0.0;
        for(float i = -1.0; i < 1.1; i+=1.0) {
            for(float j = -1.0; j < 1.1; j+=1.0) {
                if(i==j && j==0.0) continue;
                neighbor+=texture(iChannel0,vec2((scaleCoord.x+i*scale)/iResolution.x,(scaleCoord.y+j*scale)/iResolution.y)).x;

            }
           }
            if(neighbor <2.0) fragColor = vec4(0.0,0.0,0.0,1.0);
            else if(neighbor > 3.0) fragColor =vec4(0.0,0.0,0.0,1.0);
            else if(neighbor==3.0) fragColor = vec4(1.0);



    }
    if(pause) fragColor+=vec4(0.0,0.0,1.0,1.0);
    else fragColor = fragColor.xxxx;
}
