void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;

    uv.x *= iResolution.x / iResolution.y;
    vec2 uv2 = fract(sin(uv / 5.0) * 10.0 * sin(iTime + 100.0));

    uv = fract(uv * 5.0 * sin(iTime)) - 0.5;

    float d = length(uv);
    d *= d * 0.75 * cos(iTime) * sin(iTime);

    float mult = 1.0;
    d = sin(d) * 10.0 + 5.0 * cos(d*5.0);

    float newTime = sin(iTime);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime + newTime * 5.0 + uv2.xyx+vec3(0,2,4) * d);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
