// It applies correct (at center) pivoting on both fixed and warped aspect ratio
// mainly because I didn't feel like adding another keyword.

// Aliases
#define UV fragCoord
#define FRAG fragColor
#define ROTMAT rotationMatrix
#define ROTFAC rotationSpeed
#define TILFAC tilingFactor
#define TRANSX translationSpeedX
#define TRANSY translationSpeedY

// These keywords enable and disable shader features
#define TILING
#define ASPECT_RATIO_CORRECTION
#define AUTOMATIC_ROTATION
//#define AUTOMATIC_TRANSLATION_X
//#define AUTOMATIC_TRANSLATION_Y
define DEBUG_VIEW

// Adjustable Variables
float tilingFactor = 5.0;
// Speed variables control translation/rotation amount if AUTOMATIC keywords are disabled
// If AUTOMATIC keywords are enabled, they act as a multiplier to iTime
float translationSpeedX = 0.01;
float translationSpeedY = 0.01;
float rotationSpeed = 0.1;

// Helper functions
mat2 rotationMatrix(in float a) { float c=cos(a), s=sin(a); return mat2(c,s,-s,c); }
vec2 GET_UV(inout vec2 uv) 
{ 
    #ifdef ASPECT_RATIO_CORRECTION
    // Pivoting done by subtracting 0.5 before correcting AR
    uv.xy = ((uv.xy-0.5*iResolution.xy)/iResolution.y);
    #else
    // Pivoting done by performing a *2-1 after doing the iResolution thing
    uv.xy = ((uv.xy/iResolution.xy)*vec2(2.0)-vec2(1.0));
    #endif

    #ifdef AUTOMATIC_ROTATION
    uv *= ROTMAT(iTime*ROTFAC);
    #else
    uv *= ROTMAT(ROTFAC);
    #endif

    #ifdef TILING
    uv*=vec2(TILFAC);
    #endif

    #ifdef ASPECT_RATIO_CORRECTION
    uv= uv+0.5;
    #else
    uv= uv*0.5+0.5;
    #endif

    #ifdef AUTOMATIC_TRANSLATION_X
    uv.x += (TRANSX+iTime);
    #else
    uv.x += (TRANSX);
    #endif

    #ifdef AUTOMATIC_TRANSLATION_Y
    uv.y += (TRANSY+iTime);
    #else
    uv.y += (TRANSY);
    #endif
    return uv;
}
vec4 GET_COLOR(inout vec4 frag, vec2 uv) 
{ 
    frag = vec4(1.0);
    #ifdef DEBUG_VIEW
    frag.xyz = vec3(uv,0.0);
    #else
    frag.xyz = vec3(texture(iChannel0,uv).xyz);
    #endif
    return frag;
}

// Fragment program
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    GET_UV(UV);
    GET_COLOR(FRAG,UV);
}
