float line(vec2 UV,vec2 b,vec2 offset){

    vec2 d = abs(UV+offset)-b;

    return  floor(length(max(d,0.)) + min(max(d.x,d.y),1.));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = .5-fragCoord.xy/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    vec2 iuv =uv *10.256;
	vec4 a = vec4(0.,0.,0.,1.);
    vec4 b= vec4(1.,1.,1.,1.);

    float thickness = -0.424;

    vec2 v = vec2(thickness,10.);
    vec2 h = vec2(10.,thickness);
    vec4 fond = vec4(1.,1.,1.,1.);
    vec4 color = vec4(1.);
    //vertical
    color = mix(a,b,line(iuv,v,vec2(-2.184,-2.184)));
    color *= mix(a,b,line(iuv,v,vec2(-1.408)));
    color *= mix(a,b,line(iuv,v,vec2(-0.936)));
    color *= mix(a,b,line(iuv,v,vec2(-0.616)));
    color *= mix(a,b,line(iuv,v,vec2(0.464)));
    color *= mix(a,b,line(iuv,v,vec2(1.656)));
    color *= mix(a,b,line(iuv,v,vec2(1.992)));
    color *= mix(a,b,line(iuv,v,vec2(2.640)));

    //horizontal
    color *= mix(a,b,line(iuv,h,vec2(2.3)));
    color *= mix(a,b,line(iuv,h,vec2(1.1)));
    color *= mix(a,b,line(iuv,h,vec2(.2)));
    color *= mix(a,b,line(iuv,h,vec2(-1.)));

    color *= step(-0.308,uv.x);
    color *= step(-0.404,uv.y);
    color *= step(-0.348,-uv.x);
    color *= step(-0.356,-uv.y);

    color = mix(vec4(0.,0.,0.,1.),fond,color);
    //Square
    color *= mix(vec4(1.,1.,0.,1.),vec4(1.),line(iuv,vec2(1.,2.),vec2(-3.7,4.79)));
    color *= mix(vec4(0.,0.,1.,1.),vec4(1.),line(iuv*5.,vec2(3.8,0.2),vec2(-7.,-13.)));
    color *= mix(vec4(1.,0.,0.,1.),vec4(1.),line(iuv*2.,vec2(.7,.75),vec2(2.1,-3.35)));
    color *= mix(vec4(1.,0.,0.,1.),vec4(1.),line(iuv*5.,vec2(1.2,.3),vec2(15,-12.5)));

    color *= mix(vec4(0.,0.,0.,1.),vec4(1.),line(iuv*7.,vec2(5.,.1),vec2(8.5,-16.3)));

    color *= mix(vec4(0.,0.,0.,1.),vec4(1.),line(iuv*7.,vec2(5.,.1),vec2(8.5,-21.7)));



    fragColor = vec4(color);
}
