// used this video: https://www.youtube.com/watch?v=f4s1h2YETNY
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    uv *= 2.0;
    uv = fract(uv);
    uv -= 0.5;

    vec3 initColor = vec3(1., 2.0,3.0) * tan(fract(iTime));

    float d = length(uv);
    d = sin(d*4.-iTime)/4.;
    d = abs(d);

    d = 0.06 / d;

    //d = smoothstep(0.0, 0.1, d);

    initColor *= d;

    fragColor = vec4(initColor,1);
}
