const float pi = 3.14159265358979323846264338327950288;

float mapmove(vec2 uv, float speed)
{

    float map = pow(abs(uv.x-0.5),0.1)*pi;
    map *= 10.;
    map -= iTime*speed;
    return map;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    float ctrst = exp(uv.y+1./2.);

    vec3 col2 = vec3(sin(mapmove(uv,2.)),sin(mapmove(uv,3.)),sin(mapmove(uv,9.)));
    col2 += ctrst/2.;
    col2 /= ctrst;
    // Output to screen
    fragColor = vec4(col2,1.0);
}
