// Inspired from Sine Puke II, by WAHa.06x36.
#define TWO_PI 6.28318530718
vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b * cos(6.28318 * (c * t + d));
}

vec3 palette(float x) {
    return pal(x, vec3(0.5, 0.5, 0.5), vec3(0.5, 0.5, 0.5), vec3(1.0, 1.0, 1.0), vec3(0.0, 0.10, 0.20));
}

void ring_fire(in vec2 p_in, out vec3 color) {

    vec2 p = 1. * p_in;
    float offset = 1.;
    float a = 0.79;
    a = 0.012;
    float size = 50.;
    float speed = 2.;//m.x*10.;
    float width = 8.;
    float ratio = 1.2;//.+m.y/20.;
    for(float i = 1.; i <= size; i++) {
        p += vec2(a * sin(i * p.y + iTime * 4. + speed) + offset, a * sin(ratio * i * p.x + iTime * 4. + speed) - offset);
    }

    float dx = width * (p.x - (size) * offset);
    float dy = width * (p.y + (size) * offset);
    //float d = mod(atan(dy,dx),TWO_PI);
    float z = 0.5;
    float d = exp(z - 3.) * pow(dx * dx + dy * dy - dx, 0.8) - z;
    //float d = angle;
    //d=pow(d,0.9);
	//d=0.5/d;
    color = palette(d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y; 

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col2;
    ring_fire(uv, col2);
    // Output to screen
    fragColor = vec4(col2,1.0);
}
