// loopless + golfing 533 "Sphere fake 3d" by SnackEater. https://shadertoy.com/view/dljfWW
// 272 without Jacobians -  288 with approx J  - 308 with exact J


#define ROT mat2(cos( iTime +vec4(.5,33,11,0.)))

void mainImage( out vec4 COR, vec2 XY ) {

    bool esq = int(XY.y)%2==0;
    //if (!esq) {
    //     XY.y = XY.y-1.;
    //    return;
   // }


    vec3  R = iResolution, P;
    XY = ( 2.*XY - R.xy );
    XY /= R.y;


    float S = (1.+sin(.101/iTime))*.0015,
          Z = 1. - dot(XY,XY);

    Z*= esq?.7:.2;

    COR = vec4(esq?1.:0., esq?0.:1., 0., 1.);
    //COR *= 0.;
    Z > 0.                                                         // if on sphere
      ? 
        P = vec3( XY, pow(Z,.5) ),                                    // 3D point on front half-sphere
        P.yz *= ROT,
        P.xz *= ROT,                                                 // rotate on sphere
        XY =  vec2( atan(length(P.xy),P.z), atan(P.x,P.y) ),        // get parameterization
        COR.g= 1. - length( ( mod( XY + S , 2.*S ) -S )               // draw blob at grid centers
                          // / fwidth(XY) ) * 140./R.y                // compensate distorsion with Jacobian (approx)
                          * inverse(transpose(mat2(dFdx(XY),dFdy(XY)))) )  * 1e2/R.y  // ( precise )
      : S
      ;
}
