const int NUMBALLS = 10;

//Values for the balls
const float r[NUMBALLS]= float[NUMBALLS](0.01, 0.02, 0.03, 0.024, 0.014, 0.02, 0.03, 0.032,  0.02, 0.03);
const vec2 p0[NUMBALLS] = vec2[NUMBALLS](vec2(0.1, 0.4), vec2(0.1, 0.4), vec2(0.1, 0.4), vec2(0.1, 0.4),
                                        vec2(0.1, 0.4), vec2(0.1, 0.4), vec2(0.1, 0.4), vec2(0.1, 0.4),
                                        vec2(0.1, 0.4), vec2(0.1, 0.4));                                       
const vec2 v0[NUMBALLS] = vec2[NUMBALLS](vec2(0.04, 0.03),vec2(0.034, 0.027), vec2(0.013, 0.039), vec2(0.1, 0.03),
                                     vec2(0.063, 0.036), vec2(0.032, 0.041), vec2(0.07, 0.064), vec2(0.029, 0.031),
                                     vec2(0.013, 0.08), vec2(0.02, 0.03));
//Values for the "box"
const float d1 = 1.0; 
const float d2 = 1.0;
const vec2 pb = vec2(0.0, 0.0);
const vec2 Box = vec2(pb.x + d1,pb.y + d2); 

void mainImage(out vec4 fragColor, in vec2 fragCoord){

    float t = iTime *10.0;
    int i=0;

    fragColor = vec4(0.0);
    for(i=0; i< NUMBALLS; i++){
        vec2 q = vec2(d1 - 2.0*mod(p0[i].x + v0[i].x * t, d1), d2 - 2.0*mod(p0[i].y + v0[i].y * t, d2)); //qx(t) graph looks like ////
        vec2 p = Box - abs(q);
        float d = length(fragCoord - p * vec2(iResolution.x, iResolution.y));                            //abs(qx) looks like \/\/\/\. Thats the "bouncy" effect

        if (d < r[i] * iResolution.x) {
                //Color does not make any sense, I just wanted something trippy
                fragColor = vec4( abs(sin(t+3.0)) ,abs(cos(t-10.0)), abs(cos(t)), atan(t*1.0));
            }
    }
}
