vec2 mainSound(int samp, float time){
  float s1 = 8.;
  float tempo = s1;
  tempo = pow(2.,fmod(time*tempo,3.));
  time /= tempo/pow(2.,fmod(time/tempo,3.))*2.;
  float s2 = tempo,
  t=
      time*tempo
  ,
  m1 =
      fmod(t*tempo,s2)
  ,
  m3 =
      (fmod(t,abs(s2*tempo-m1)+1.)-m1);
  t /=
      (1.+fmod(t*tempo/s1,2.))*4./s1/s1
  ;
  float m2 =
      1. + fmod(t/s1,s1)
  ;
  float a=
      64.*(1.-sqrt(fract(t/s1)/1.25))*.2
  ,
  nb = time*tempo*pow(2.,floor((m3+m2)/2.)/5.+5.5);
  return
      abs(.5-vec2(fract(nb*.998*2.),fract(nb)))*a

      //sitar
      //log(abs(a/16.-abs(.5-vec2(fract(nb*.998),fract(nb)))*a))
  ;
}
