#ifdef GL_ES
precision mediump float;
#endif
vec3 draw_line(vec2 p1, vec2 p2, vec2 st, vec3 bc, vec3 sc){
    vec2 w=st-p2;
    vec2 u=p1-p2;
    float s=clamp(dot(w,u)/dot(u,u),0.,1.);
    bc=mix(bc,sc,smoothstep(2./iResolution.y,0.,length(w-s*u)));
    return bc;
}

vec2 rot2d(vec2 p, float dir){
    dir=radians(dir);
    return mat2(cos(dir),sin(dir),
               -sin(dir),cos(dir))*p;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 st = ( fragCoord - .5*iResolution.xy )/iResolution.y;
	vec2 mouse=( iMouse.xy - .5*iResolution.xy )/iResolution.y;
	vec2 target_c = vec2(0.);

    vec3 color=vec3(1.);
	vec3 line_col;
    float len = 0.4;
	float b = 60.;
    vec2 f = vec2(0.,.2);

	if(dot(normalize(f),normalize(target_c-mouse))>=cos(radians(b/2.))){
        line_col=vec3(1.,0.,0.);
    }
    else{
        line_col=vec3(.7);
    }

    color=draw_line(mouse,mouse+f,st,color,line_col);
    color=draw_line(mouse,mouse+rot2d(vec2(0.,len),b/2.),st,color,line_col);
    color=draw_line(mouse,mouse+rot2d(vec2(0.,len),-b/2.),st,color,line_col);

    color=mix(color,vec3(0.,0.,1.),smoothstep(.01,.01-2./iResolution.y,length(target_c-st)));
    color=mix(color,vec3(.5),smoothstep(.01,.01-2./iResolution.y,length(mouse-vec2(0.,.01)-st)));

    fragColor=vec4(color,1.);
}
