#define PIXELS_X 40.0
#define PIXELS_Y 20.0

float rand(float n){return fract(sin(n) * 43758.5453123);}

float noise(float p){
	float fl = floor(p);
  float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc) * 0.5 + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 pixelDiv = vec2(PIXELS_X, PIXELS_Y);
    vec2 uv = floor(fragCoord/iResolution.xy * pixelDiv) / pixelDiv;

    float ns = 
        noise(uv.x * 10.0 + iTime * 1.5) * 1.3 + 
        noise(uv.x * 5.0 - iTime * 3.5) * 1.6;

    ns = ns * 0.3 + 0.1;
    float color = step(ns, 1.0 - uv.y);


    // Output to screen
    fragColor = vec4(vec3(color), 1.0);
}
