#define aa 2./iResolution.y/2.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord - iResolution.xy*.5)/iResolution.y; // normalize space

    vec2 ringP = vec2(p.x+sin(iTime-3.14)*0.01, p.y*3.);
    float ringDist = length(ringP);
    float ringMask = smoothstep(0.+aa, 0.-aa,ringDist-0.75) - smoothstep(0.+aa, 0.-aa,ringDist-0.45);
    ringMask -= (smoothstep(0.+aa, 0.-aa,ringDist-0.55) - smoothstep(0.+aa, 0.-aa,ringDist-0.52))*0.4;
    ringMask -= (smoothstep(0.+aa, 0.-aa,ringDist-0.69) - smoothstep(0.+aa, 0.-aa,ringDist-0.63))*0.3;
    ringMask -= (smoothstep(0.+aa, 0.-aa,ringDist-0.74) - smoothstep(0.+aa, 0.-aa,ringDist-0.72))*0.5;

    float ringMaskFront = ringMask*step(p.y, 0.);
    float ringMaskBack = ringMask*(1.-step(p.y, 0.));

    vec2 planetP = p + vec2(sin(iTime)*0.01,0.);
    float planetSize = 0.3;
    float planetDist = length(planetP);
    float planetMask = smoothstep(0.+aa, 0.-aa,planetDist-planetSize);
    planetMask = clamp(planetMask + clamp(((1.-planetDist)*10.)-6.7,0.,1.),0.,1.);

    vec3 spaceColor = vec3(0.15,0.,0.25);
    spaceColor = mix(spaceColor, vec3(0.), length(p)/1.2);

    vec3 ringColor = vec3(0.85,0.95,0.7);
    ringColor = mix(ringColor, vec3(0.3,0.8,0.7), (sin(pow(ringDist*(1./0.75),4.)*12.)+1.)/2.);

    vec3 planetColor = vec3(1.,0.9,0.5);
    planetColor = mix(planetColor, vec3(0.3,0.6,0.6), pow(planetDist*(1./planetSize),7.));

    vec3 col = mix(spaceColor, ringColor, ringMaskBack);
    col = mix(col, planetColor, planetMask);
    col = mix(col, ringColor, ringMaskFront);

    fragColor = vec4(col,1.0);
}
