#define P 3.14159265
#define R iResolution
#define T iTime

vec4 C (vec2 u, vec3 C) {
    float c = smoothstep(.05/R.y, 0., length(u) - .1);
    return vec4(C + smoothstep(-0.3, 0.5, u.y - u.x*.5) * c, c);
}
void mainImage( out vec4 O, in vec2 I )
{
    vec2 uv = (I-.5*R.xy)/R.y;
    O = vec4(1.);
    for (float i = 1.; i > 0.; i -= 1./300.) {
        float y = sin(T*2. + i * P * 5.2) * .12;
        vec4 c = C(uv - vec2(
        -.2 + sin(i * P * 6. - T * 2. - sin(T + i * 3.) * 4.) * .1 + i * .4, 
        -.2 + cos(i * P * 6. - T * 2. - sin(T + i * 3.) * 4.) * .1 + i * .4), 
        vec3(.15 + i*.6, .5 - i*.3, 1.));
        O = mix(O, c, c.a);
    }
}
