const float M_E = 2.71828182845904523536028747135266250;

float get_factor_for_line_drawing(float thickness, float pos_offset, float pos_input) {
    return 1. - pow(M_E, -thickness * pow(pos_input - pos_offset,8.));
}

float get_factor_for_point(float thickness_mod, float pos_offset_x, float pos_offset_y, float pos_input_x, float pos_input_y) {
    float factor_x = get_factor_for_line_drawing(thickness_mod, pos_offset_x, pos_input_x);
    float factor_y = get_factor_for_line_drawing(thickness_mod, pos_offset_y, pos_input_y);
    float final_line_fac = 1. - (factor_x + factor_y);
    return max(0.0, min(final_line_fac, 1.));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    float thickness_mod = 200000000000000000.;
    float pos_offset_x = 0.5 + 0.5*cos(0.52*iTime);
    float pos_offset_y = 0.5 + 0.5*cos(0.7343*iTime);
    float pos_offset_x2 = 0.5 + 0.5*cos(0.32*iTime);
    float pos_offset_y2 = 0.5 + 0.5*cos(0.1343*iTime);

    float point_1 = get_factor_for_point(thickness_mod, pos_offset_x, pos_offset_y, uv.x, uv.y);
    float point_2 = get_factor_for_point(thickness_mod, pos_offset_x2, pos_offset_y2, uv.x, uv.y);
    float final_fac = point_1 + point_2;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(final_fac*col,1.0);
}
