const float amount = 0.3;

const float ordinary[5] = float[5](-amount, -amount, amount *  4.0 + 1.0, -amount, -amount);
const float left[5]     = float[5](-amount, amount * 3.0, amount + 1.0, amount * -2.0, -amount);
const float right[5]    = float[5](-amount, -amount, amount + 1.0, amount * 2.0, -amount);

const vec2 offsets[5]   = vec2[5](vec2( 0,  1), vec2(-1,  0), vec2( 0,  0), vec2( 1,  0), vec2( 0, -1));

float insideMouseRange(float x, float halfRange) // only return 0 or 1
{
    return step(iMouse.x - halfRange, x) * step(x, iMouse.x + halfRange);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    // float amount = 0.3;

    // float neighbor = amount * -1.0;
    // float center   = amount *  4.0 + 1.0;

    vec4 color =
          texture(iChannel0, (fragCoord + offsets[0]) / iResolution.xy) * right[0]
        + texture(iChannel0, (fragCoord + offsets[1]) / iResolution.xy) * right[1]
        + texture(iChannel0, (fragCoord + offsets[2]) / iResolution.xy) * right[2]
        + texture(iChannel0, (fragCoord + offsets[3]) / iResolution.xy) * right[3]
        + texture(iChannel0, (fragCoord + offsets[4]) / iResolution.xy) * right[4];

    fragColor = mix(mix(color, texture(iChannel0, uv), step(iMouse.x, fragCoord.x)),
                    vec4(0.227, 0.239, 0.255,1.0), insideMouseRange(fragCoord.x, 1.));
}
